<?php$fget='home';$task='alap';GOB::$html=file_get_contents('tmpl/flat/base.html', true);if(isset($_GET['task'])){$task=$_GET['task'];}if(isset($_POST['task'])){$task=$_POST['task'];}if(isset($_GET['fget'])){$fget=$_GET['fget'];}//tartalom elősáálítása----------------------$tartalom="nincs tartalom";switch ($fget) {case 'login'://modulok becsatolása------------$tartalom=MOD::login();break;case 'mas modul'://  echo "i equals 1";break;default:  //file becsatolás-----------include_once 'app/base/'.$fget.'.php';ADT::$view=ViewS::result($task);ADT::$datatomb=DataS::result($task);$tartalom=AppS::result($task);}//lap generálás a tartalommal-----------------------------------------GOB::$html= str_replace('<!--|tartalom|-->',$tartalom ,GOB::$html);//nyelvi elemek feltöltése---------------------------------GOB::$html=LANG::db_feltolt(GOB::$html,ADT::$datatomb);GOB::$html=LANG::LT_feltolt(GOB::$html);echo GOB::$html;